    def __init__(self, wrapped_bar, wandb_project, run_name=None):
        super().__init__(
            wrapped_bar, epoch=wrapped_bar.epoch, prefix=wrapped_bar.prefix
        )
        self.wrapped_bar = wrapped_bar
        if wandb is None:
            logger.warning("wandb not found, pip install wandb")
